import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NewPost = () => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [images, setImages] = useState(['', '', '']);
    const navigate = useNavigate();

    const handleImageChange = (index, value) => {
        const newImages = [...images];
        newImages[index] = value;
        setImages(newImages);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = { title, content, images };
        const storedPosts = JSON.parse(localStorage.getItem('posts')) || [];
        storedPosts.push(newPost);
        localStorage.setItem('posts', JSON.stringify(storedPosts));
        navigate('/');
    };

    const inputStyle = {
        width: '100%',
        fontSize: '16px',
        padding: '8px',
        margin: '8px 0',
    };

    const textareaStyle = {
        width: '100%',
        height: '150px',
        fontSize: '16px',
        padding: '8px',
        margin: '8px 0',
    };

    return (
        <div className="new-post-container">
            <h1 className="new-post-title">New Post</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label className="new-post-label">Title:</label>
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                        style={inputStyle}
                    />
                </div>
                <div>
                    <label className="new-post-label">Content:</label>
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        required
                        style={textareaStyle}
                    ></textarea>
                </div>
                <div>
                    <label className="new-post-label">Image 1 URL:</label>
                    <input
                        type="text"
                        value={images[0]}
                        onChange={(e) => handleImageChange(0, e.target.value)}
                        required
                        style={inputStyle}
                    />
                    {images[0] && <img src={images[0]} alt="Preview 1" style={{ width: '100px', height: '100px' }} />}
                </div>
                <div>
                    <label className="new-post-label">Image 2 URL:</label>
                    <input
                        type="text"
                        value={images[1]}
                        onChange={(e) => handleImageChange(1, e.target.value)}
                        required
                        style={inputStyle}
                    />
                    {images[1] && <img src={images[1]} alt="Preview 2" style={{ width: '100px', height: '100px' }} />}
                </div>
                <div>
                    <label className="new-post-label">Image 3 URL:</label>
                    <input
                        type="text"
                        value={images[2]}
                        onChange={(e) => handleImageChange(2, e.target.value)}
                        required
                        style={inputStyle}
                    />
                    {images[2] && <img src={images[2]} alt="Preview 3" style={{ width: '100px', height: '100px' }} />}
                </div>
                <button type="submit">Add Post</button>
            </form>
        </div>
    );
};

export default NewPost;
