import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NewPost = () => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [images, setImages] = useState(['', '', '']);
    const navigate = useNavigate();

    const handleImageChange = (index, file) => {
        const reader = new FileReader();
        reader.onloadend = () => {
            const newImages = [...images];
            newImages[index] = reader.result;
            setImages(newImages);
        };
        reader.readAsDataURL(file);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = { title, content, images };
        const storedPosts = JSON.parse(localStorage.getItem('posts')) || [];
        storedPosts.push(newPost);
        localStorage.setItem('posts', JSON.stringify(storedPosts));
        navigate('/');
    };

    const inputStyle = {
        width: '100%',
        fontSize: '16px',
        padding: '8px',
        margin: '8px 0',
    };

    const textareaStyle = {
        width: '100%',
        height: '150px',
        fontSize: '16px',
        padding: '8px',
        margin: '8px 0',
    };

    return (
        <div>
            <h1>New Post</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Title:</label>
                    <input
                        type="text"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                        style={inputStyle}
                    />
                </div>
                <div>
                    <label>Content:</label>
                    <textarea
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        required
                        style={textareaStyle}
                    ></textarea>
                </div>
                <div>
                    <label>Image 1:</label>
                    <input
                        type="file"
                        onChange={(e) => handleImageChange(0, e.target.files[0])}
                        required
                        style={inputStyle}
                    />
                    {images[0] && <img src={images[0]} alt="Preview 1" style={{ width: '100px', height: '100px' }} />}
                </div>
                <div>
                    <label>Image 2:</label>
                    <input
                        type="file"
                        onChange={(e) => handleImageChange(1, e.target.files[0])}
                        required
                        style={inputStyle}
                    />
                    {images[1] && <img src={images[1]} alt="Preview 2" style={{ width: '100px', height: '100px' }} />}
                </div>
                <div>
                    <label>Image 3:</label>
                    <input
                        type="file"
                        onChange={(e) => handleImageChange(2, e.target.files[0])}
                        required
                        style={inputStyle}
                    />
                    {images[2] && <img src={images[2]} alt="Preview 3" style={{ width: '100px', height: '100px' }} />}
                </div>
                <button type="submit">Add Post</button>
            </form>
        </div>
    );
};

export default NewPost;
